import React from 'react';
import { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import moment from 'moment';

//function Clock

// function startButton(props){
//         return(
//         <button onClick={this.props.handleStart} className="startButton"> START </button>
//         );
// }
//
// function twoButtons(props){
//     let makeLapse = () => {
//         this.props.onLap({
//             id: this.props.idLapse + 1,
//             end_time: "tatata",
//             duration: "tatata",
//         });
//     };
//
//         return (
//     (this.props.resume === true)? <div><button>STOP</button><button onClick={this.makeLapse}>LAP</button></div>
//     : <div><button>RESUME</button><button onClick={this.props.onReset}>RESET</button></div>
//     );
// }

function LapsesTable() {
    const [start, setStart] = useState(false);
    const [stop, setStop] = useState(false);
    const [resume, setResume] = useState(false);

    // const [idLapse, setIdLapse] = useState(0);
    //
    // const [lapses, setLapses] = useState([]);
    //
    // let Lap = lapse => {
    //     setIdLapse(idLapse + 1);
    //     setLapses([lapse, ...lapses]);
    // };

   /* let handleReset = () => {
        setStart(false);
        setStop(false);
        setResume(false);
        setIdLapse(0);
        setLapses([]);
    };*/

    /*return (
        ({start} === false ?
            <div>
                <startButton onClick={() => setStart(!start)}/>
            </div> :
            <div>
                <twoButtons onReset={() => this.handleReset()}
                            onLap={() => this.Lap()} idLapse={idLapse}

                />
                <div className={LapsesList}>
                    <hr/>
                    {lapses.map(lapse => (
                        <Lapse
                            key={lapse.id}
                            lapse={lapse}
                        />
                    ))}
                </div>
            </div>)
    );*/
};

function twoButtons(props){
    let makeLapse = () => {
        this.props.onLap({
            id: this.props.idLapse + 1,
            end_time: "tatata",
            duration: "tatata",
        });
    };

    return (
        (this.props.resume === true)?
            <div className="twoButtons">
                <button className="stopButton">STOP</button>
                <button onClick={this.makeLapse}>LAP</button>
            </div>
            :
            <div className="twoButtons">
                <button>RESUME</button>
                <button onClick={this.props.onReset}>RESET</button>
            </div>
    );
}

function Lapse(props){
    return (
        <div className="Lapse">
            <span>{props.number}</span>
            <Timer interval={props.lapse.end_time}/>
            <Timer interval={props.lapse.duration}/>
            {/*<span>{props.lapse.end_time}</span>*/}
            {/*<span>{props.lapse.duration}</span>*/}
        </div>
    );
}

function LapsesList(props) {
    return (
        <div className={LapsesList}>
            <hr/>
            {props.lapses.map((lapse, index) => (
                <Lapse
                    lapse = {lapse}
                    key = {props.lapses.length - index}
                    number = {props.lapses.length - index}
                />
            ))}
        </div>
    );
}

function Timer({interval}) {
    const [duration, setDuration] = useState(moment.duration(interval));
    const milliseconds = () => {
        if (duration.milliseconds() < 10) {
            return "0" + Math.round(duration.milliseconds() / 10);
        } else {
            return Math.round(duration.milliseconds() / 10);
        }
    }
    const seconds = () => {
        if(duration.seconds()<10){
            return "0"+duration.seconds();
        }
        else{
            return duration.seconds();
        }
    };
    return (
        <div>{duration.minutes()}:{seconds()}.{milliseconds()}</div>
    )
}

function StartButton(props){
    return(
        <button onClick={props.onStart} className="startButton"> START </button>
    );
}

// function rButton(props){
//     return(
//         <button onClick={props.onPress} className={props.class}>{props.title}</button>
//     )
// }

function App() {
    // const [timer, setTimer] = useState(1234567);
    const [idLapse, setIdLapse] = useState(0);
    const [lapses, setLapses] = useState([{end_time:12345,duration:1234},{end_time:12345,duration:2345},{end_time:12345,duration:34567}]);
    const [now, setNow] = useState(0);
    const [start, setStart] = useState(0);
    //const [interval,setInterv] = useState(Number(now)-Number(start));
    //const [lapses, setLapses] = useState([]);

    //const [isStarted, setStart] = useState(false);
    // const [stop, setStop] = useState(false);
    const [resume, setResume] = useState(false);

    let addLapse = lapse => {
        setLapses([...lapses,lapse]);
    };

    let Lap = lapse => {
        setIdLapse(idLapse + 1);
        setLapses([...lapses,lapse]);
    };

    // let handleReset = () => {
    //     setStart(false);
    //     setStop(false);
    //     setResume(false);
    //     setIdLapse(0);
    //     setLapses([]);
    // };
    let handleStart = () => {
        setStart(new Date().getTime());
        setLapses([{end_time: 0, duration: 0}]);
        setNow(setInterval(()=> new Date().getTime(),100));
    };

    // let buttons = () => {
    //     if (true) {
    //         {
    //          return (<div>
    //                 <startButton onStart={() => handleStart()}/>
    //             </div>);
    //         }
    //     }
    //     else {
    //         return (<div>
    //             <twoButtons resume={resume} onReset={() => this.handleReset()}
    //                         onLap={() => this.Lap()} idLapse={idLapse}
    //             />
    //         </div>);
    //     }
    // };

    // let buttons = () => {
    //     if (true) {
    //         return (<div>
    //             <twoButtons resume={resume} onReset={() => this.handleReset()}
    //                         onLap={() => this.Lap()} idLapse={idLapse}
    //             />
    //         </div>);
    //     } else {
    //         return (<div>
    //             <startButton onStart={() => handleStart()}/>
    //         </div>);
    //     }
    // };

    return (
        <div className="App">
            <div className="Timer">
                <Timer interval={Number(now)-Number(start)} />
            </div>

             <div className="Buttons twoButtons">
                 <button onClick={()=>handleStart()} className="startButton">START</button>
                 <button  className="resetButton">RESET</button>
            </div>
            <LapsesList lapses={lapses}/>
        </div>
       );
}

ReactDOM.render(<App />, document.getElementById("root"));
