import React from 'react';
import { useState, useEffect , useRef} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import moment from 'moment';

function Lapse(props){
    return (
        <div className="Lapse">
            <span>Lapse {props.number}</span>
            <Timer interval={props.end_time}/>
            <Timer interval={props.duration}/>
            {/*<button className="deleteButton" onClick={props.onDelete}>x </button>*/}
            {/*<span>{props.lapse.end_time}</span>*/}
            {/*<span>{props.lapse.duration}</span>*/}
        </div>
    );
}

function LapsesList(props) {
    return (
        <div className="LapsesList">
            {/*<hr/>*/}
            {props.lapses.map((lapse, index) => (
                <Lapse
                    // lapse = {lapse}
                    end_time = {lapse.end_time}
                    duration = {lapse.duration}
                    key = {props.lapses.length - index}
                    number = {props.lapses.length - index}
                />
            ))}
        </div>
    );
}

function Small_stroke(props){
    let style = props.mStyle.slice(1, props.mStyle.length-2);
    return(
        <span style={{style}} className={props.class}></span>
    )
}
function Stroke(props){
    let style = props.mStyle.slice(1, props.mStyle.length-2);
    return(
        <span style={{style}} className={props.class}></span>
    )
}

function StopWatch(interval){
    const [duration, setDuration] = useState(moment.duration(interval));
    useEffect(() => setDuration(moment.duration(interval)), [interval]);
    const seconds = duration.seconds();
    const minutes = duration.minutes();

    let secondsStartDegree = 360 / 60 * seconds;
    let minutesStartDegree = 360 / 60 * minutes + 6 / 60 * seconds;

    // let style = document.createElement('style');
    //
    // style.innerHTML = '
    //     @keyframes clock-hand-rotate--minute {
    //         from {transform: rotate(' + minutesStartDegree + 'deg)}
    //         to {transform: rotate(' + (minutesStartDegree + 360) + 'deg)}
    //     }
    //     @keyframes clock-hand-rotate--second {
    //         from {transform: rotate(' + secondsStartDegree + 'deg)}
    //         to {transform: rotate(' + (secondsStartDegree + 360) + 'deg)}
    //     }
    //     .clock__hand--minute {
    //         animation: clock-hand-rotate--minute 3600s linear infinite;
    //     }
    //     .clock__hand--second {
    //         animation: clock-hand-rotate--second 60s linear infinite;
    //     }' ;
    //
    // document.getElementsByTagName('root')[0].appendChild(style);

    let stopAnimation = (isStopped) => {
        if(isStopped === true){
            let element = document.getElementsByName('style');
            element.remove();
        }
        else{
            let style = document.createElement('style');

            style.innerHTML =
                '@keyframes clock-hand-rotate--minute {\
                from {transform: rotate(' + minutesStartDegree + 'deg)}\
                to {transform: rotate(' + (minutesStartDegree + 360) + 'deg)}\
            }\
        @keyframes clock-hand-rotate--second {\
                from {transform: rotate(' + secondsStartDegree + 'deg)}\
                to {transform: rotate(' + (secondsStartDegree + 360) + 'deg)}\
            }\
        .clock__hand--minute {\
                animation: clock-hand-rotate--minute 3600s linear infinite;\
            }\
        .clock__hand--second {\
                animation: clock-hand-rotate--second 60s linear infinite;\
            }' ;

            document.getElementsByTagName('root')[0].appendChild(style);
        }
    };

    let strokes = () => {
        let j=0;
        let cycle = () => {
            for (let i = 1; i < 61; i++) {
                j = 6 * i;
                if (i % 5 !== 0) {
                    <small_stroke mStyle={`transform: rotate(${j}+deg)`} class="clock__stroke clock__stroke--small"/>
                } else {
                    <stroke mStyle={`transform: rotate(${j}deg);`} class="clock__stroke"/>
                }
            }
        };
        cycle();
    };
    return (
        <div>
            {strokes()}
            <span className="clock__hand clock__hand--minute"></span>
            <span className="clock__hand clock__hand--second"></span>
            {stopAnimation()}
        </div>
    )
}

function Timer({interval}) {
    const [duration, setDuration] = useState(moment.duration(interval));
    useEffect(() => setDuration(moment.duration(interval)), [interval]);
    //const milliseconds = Math.floor(duration.milliseconds() /10);
    const pad = (n) => n < 10 ? "0" + n : n ;
    //const milliseconds = pad()
    const milliseconds = Math.round(duration.milliseconds() / 10);
    const seconds = () => {
        if(duration.seconds()<10){
            return "0"+duration.seconds();
        }
        else{
            return duration.seconds();
        }
    };
    return (
        <div>{pad(duration.minutes())}:{pad(duration.seconds())}.{pad(milliseconds)}</div>
    )
}

function App() {
    const [lapses, setLapses] = useState([]);
    const [now, setNow] = useState(0);
    const [start, setStart] = useState(0);
    const [isStopped, setStop] = useState(0);

    let interval = +now-(+start);
    const [myInterval, setMyInterval] = useState(null);

    useEffect(() => {
        if (isStopped) {
            clearInterval(myInterval);
        }
        else if(start > 0){
            const id = setInterval(() => {
                setNow(new Date().getTime());
            }, 80);
            setMyInterval(id);
        }
        return () => clearInterval(timer);
    }, [isStopped]);

    let timer = () => {
        setInterval(() => {
            setNow(new Date().getTime());
        }, 80);
    };

    let addLapse = () => {
        const timestamp = new Date().getTime();
        let duration;
        lapses.length > 0 ? duration = timestamp - (lapses[0]["end_time"] + start)
        : duration = timestamp - start;
        setLapses([{end_time:timestamp-start,duration:duration},...lapses]);
        setStop(false);
    };

    let handleStart = () => {
        setStart(new Date().getTime());
        setNow(new Date().getTime());
        setStop(false);
    };

    let handleResume = () => {
      setStart(new Date().getTime()-interval);
      setNow(new Date().getTime());
      setStop(false);
    };

    let handleReset = () => {
        setNow(0);
        setStart(0);
        setStop(0);
        setLapses([]);
    };

    let handleStop = () => {
        setStop(true);
    };

    return (
        <div className="App">
            <div className="Timer">
                <Timer interval={interval} />
            </div>
            <div>
                <StopWatch interval={interval} isStopped={isStopped}/>
            </div>
            <div className="lapseTimer">
                {lapses.length > 0 ? <Timer interval={interval-lapses[0]["end_time"]} /> : <Timer interval={interval} />}
            </div>
            { (start > 0 && !isStopped)?
                (<div className="Buttons twoButtons">
                    <button onClick={()=>addLapse()} className="lapButton">LAP</button>
                    <button onClick={()=>handleStop()} className="stopButton">STOP</button>
                </div>)
                :
                (isStopped) ?
                    (<div className="Buttons twoButtons">
                        <button onClick={()=>handleResume()} className="startButton">RESUME</button>
                        <button onClick={()=>handleReset()} className="resetButton">RESET</button>
                    </div>)
                        :
                    (<div className="Buttons twoButtons">
                        <button onClick={()=>handleStart()} className="startButton">START</button>
                        <button onClick={()=>handleReset()} className="resetButton">RESET</button>
                    </div> )
            }
            <LapsesList lapses={lapses} />
        </div>
       );
}

ReactDOM.render(<App />, document.getElementById("root"));
