import React from 'react';
import { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import './index.css';

//function Clock

function Lapse(props){
    return (
        <div style={{ display: "flex", justifyContent: "center" }} className={Lapse}>
            {/*<div*/}
            {/*    onClick={props.toggleComplete}*/}
            {/*>*/}
            <span>{props.lapse.id}</span>
            <span>{props.lapse.end_time}</span>
            <span>{props.lapse.duration}</span>

            {/*<span>01</span>*/}
            {/*<span>02</span>*/}
            {/*<span>{props.lap.duration}</span>*/}
            {/*</div>*/}
           /* <button onClick={props.onDelete}>x</button>*/
        </div>
    );

}

function LapsesList(props) {
    const [lapses, setLapses] = useState([]);

    let Lap = lapse => {
        setLapses([lapse,...lapses]);
    };

    return (
        <div className={LapsesList}>
            <hr/>
            {lapses.map(lapse => (
                <Lapse
                    key={lapse.id}
                    lapse={lapse}
                />
            ))}
        </div>
    );
}

function startButton(props){
        return(
        <button onClick={this.props.handleStart} className={startButton()}> START </button>
        );
}

function twoButtons(props){
    funmakeLapse = () => {
        this.props.onLap({
            id: this.props.idLapse + 1,
            end_time: "tatata",
            duration: "tatata",
        });
    };

        return (
    (this.props.resume === true)? <div><button>STOP</button><button onClick={this.makeLapse}>LAP</button></div>
    : <div><button>RESUME</button><button onClick={this.props.onReset}>RESET</button></div>
    );
}


function LapsesTable() {
    const [start, setStart] = useState(false);
    const [stop, setStop] = useState(false);
    const [resume, setResume] = useState(false);
    const [idLapse, setIdLapse] = useState(0);

    const [lapses, setLapses] = useState([]);

    Lap = lapse => {
        setIdLapse(idLapse + 1);
        setLapses([lapse, ...lapses]);
    };

    handleReset = () => {
        setStart(false);
        setStop(false);
        setResume(false);
        setIdLapse(0);
        setLapses([]);
    };

    return (
        ({start} === false ?
            <div>
                <startButton onClick={() => setStart(!start)}/>
            </div> :
            <div>
                <twoButtons onReset={() => this.handleReset()}
                            onLap={() => this.Lap()} idLapse={idLapse}

                />
                <div className={LapsesList}>
                    <hr/>
                    {lapses.map(lapse => (
                        <Lapse
                            key={lapse.id}
                            lapse={lapse}
                        />
                    ))}
                </div>
            </div>)
    );
};

function Timer(props){
    return (
        <div>Interval:{props.interval}</div>
    )
}

function App(){
    const [timer, setTimer] = useCount(1234567);
    return (
        <Timer interval={timer}/>
    );
}


function Example() {
    // Объявляем новую переменную состояния, которую назовём "count"
    const [count, setCount] = useState(0);

    useEffect(() => {
        document.title = `Вы нажали ${count} раз`;
    });

    return (
        <div>
            <p>Вы кликнули {count} раз</p>
            <button onClick={() => setCount(count + 1)}>
                Кликни меня!
            </button>
        </div>
    );
}

ReactDOM.render(<App />, document.getElementById("root"));
