import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class InputToDo extends React.Component{
    render(){
        return(
            <input
        type = "text"
        placeholder = "What needs to be done?"
        className = "input"
        // onKeyDown = "addToList(this)"
        onKeyDown={() => this.props.addToList()}
         />
        );
    }
}
class ToDoItem extends React.Component{
    constructor(props){
        super(props);
        this.state={
            done: null,
            // data_i: null,
            data_text: null,
            value: null,
        }
    }
    render(props){
    return (
            <li>{props.value}</li>
    )
    }
}

class ToDo extends React.Component{
    constructor(props){
        super(props);
        this.state={
            ToDos: Array,
            length: this.state.ToDos.length,
        }
    }

    addToList(ele){
        if(e.keyCode == 13) {
            if(!ToDos.length){
                this.setState(ToDos= Array(ele.value));
                alert(ele.value);
            }
            else{
                this.setState(ToDos = this.ToDos.push(ele.value));
            }
        }
    }

    render(){
        return(
            <ul>{ToDos.from(this.state.ToDos, (e, i) => {
                return <li key={i}>{i}</li>
            })}</ul>
        );
        //     const ToDos = this.state.ToDos;
        //     ToDos.map((index) =>
        //     <ToDoItem value={this.state.ToDos[index]}
        //     />
        // );
    }
}

class MainToDo extends React.Component {
  render() {
    return (
        <div>
      <div className="AddToDo">
          <InputToDo />
      </div>
      <div className="List">
          <ul>
            <ToDo />
          </ul>
      </div>
      </div>
    )
  }
}

ReactDOM.render(
  <MainToDo />,
  document.getElementById('root')
);
