            </div>
            :
            <div className="twoButtons">
                <button>RESUME</button>
                <button onClick={this.props.onReset}>RESET</button>
            </div>
    );
}

function startButton(props){
    return(
        <button onClick={this.props.onStart} className="startButton"> START </button>
    );
}

function Timer(props){
    const [duration, setDuration] = useState(moment.duration(props.interval));
    const milliseconds = Math.round(duration.milliseconds()/10);
    return (
        <div>{duration.minutes()}:{duration.seconds()}.{milliseconds}</div>
    )
}

