function Timer({interval}) {
    const [duration, setDuration] = useState(moment.duration(interval));
    useEffect(() => setDuration(moment.duration(interval)), [interval]);
    //const milliseconds = Math.floor(duration.milliseconds() /10);
    const pad = (n) => n < 10 ? "0" + n : n ;
    //const milliseconds = pad()
    const milliseconds = Math.round(duration.milliseconds() / 10);
    const seconds = () => {
        if(duration.seconds()<10){
            return "0"+duration.seconds();
        }
        else{
            return duration.seconds();
        }
    };
    return (
        <div>{pad(duration.minutes())}:{pad(duration.seconds())}.{pad(milliseconds)}</div>
    )
}

function App() {
    const [lapses, setLapses] = useState([]);
    const [now, setNow] = useState(0);
    const [start, setStart] = useState(0);
    const [isStopped, setStop] = useState(0);

    let interval = +now-(+start);
    const [myInterval, setMyInterval] = useState(null);

    useEffect(() => {
        if (isStopped) {
            clearInterval(myInterval);
        }
        else if(start > 0){
            const id = setInterval(() => {
                setNow(new Date().getTime());
            }, 80);
            setMyInterval(id);
        }
        return () => clearInterval(timer);
    }, [isStopped]);

    let timer = () => {
        setInterval(() => {
            setNow(new Date().getTime());
        }, 80);
    };

    let addLapse = () => {
        const timestamp = new Date().getTime();
        let duration;
        lapses.length > 0 ? duration = timestamp - (lapses[0]["end_time"] + start)
        : duration = timestamp - start;
        setLapses([{end_time:timestamp-start,duration:duration},...lapses]);
        setStop(false);
    };

    let handleStart = () => {
        setStart(new Date().getTime());
        setNow(new Date().getTime());
        setStop(false);
    };

    let handleResume = () => {
      setStart(new Date().getTime()-interval);
      setNow(new Date().getTime());
      setStop(false);
    };

    let handleReset = () => {
        setNow(0);
        setStart(0);
        setStop(0);
        setLapses([]);
    };

    let handleStop = () => {
        setStop(true);
    };

    return (
        <div className="App">
            <div className="Timer">
                <Timer interval={interval} />
            </div>
            <div className="lapseTimer">
                {lapses.length > 0 ? <Timer interval={interval-lapses[0]["end_time"]} /> : <Timer interval={interval} />}
            </div>
            { (start > 0 && !isStopped)?
                (<div className="Buttons twoButtons">
                    <button onClick={()=>addLapse()} className="lapButton">LAP</button>
                    <button onClick={()=>handleStop()} className="stopButton">STOP</button>
                </div>)
                :
                (isStopped) ?
                    (<div className="Buttons twoButtons">
                        <button onClick={()=>handleResume()} className="startButton">RESUME</button>
                        <button onClick={()=>handleReset()} className="resetButton">RESET</button>
                    </div>)
                        :
                    (<div className="Buttons twoButtons">
                        <button onClick={()=>handleStart()} className="startButton">START</button>
                        <button onClick={()=>handleReset()} className="resetButton">RESET</button>
                    </div> )
            }
            <LapsesList lapses={lapses} />
        </div>
       );
}

ReactDOM.render(<App />, document.getElementById("root"));
